mapper.insert:
create table event_log ( id integer not null constraint event_log_pk primary key autoincrement, uuid varchar(32) not null, domain int not null, app int not null, type int not null, number int not null, timestamp bigint not null, processor varchar(64) not null, params text );
create index event_log_ts_index on event_log (timestamp);
create unique index event_log_uuid_uindex on event_log (uuid);
